export default {
  name: "runDiagnosticsTable",

  data() {
    return {
      spanArr: [], //遍历数据时，根据相同的标识去存储记录
      pos: 0, // 二维数组的索引
    };
  },

  props: {
    loading: {
      type: Boolean,
      default: true,
    },

    emptyText: {
      type: String,
      default: " ",
    },

    tableData: {
      type: Array,
      default: () => {},
    },
  },

  computed: {
    newTableData() {
      let arr = [];
      this.tableData.forEach((item) => {
        arr = [...arr, item.param];
      });
      arr = Array.from(new Set(arr));
      let newArr = [];
      arr.forEach((item) => {
        this.tableData.forEach((ele) => {
          if (ele.param === item) newArr = [...newArr, ele];
        });
      });
      this.mergeSpan(newArr);
      return newArr;
    },
  },

  mounted() {},

  methods: {
    // 合并行
    mergeSpan(data) {
      let that = this;
      //页面展示的数据，不一定是全部的数据，所以每次都清空之前存储的 保证遍历的数据是最新的数据。以免造成数据渲染混乱
      that.spanArr = [];
      that.pos = 0;
      //遍历数据
      data.forEach((item, index) => {
        //判断是否是第一项
        if (index === 0) {
          this.spanArr.push(1);
          this.pos = 0;
        } else {
          //不是第一项时，就根据标识去存储
          if (data[index].param === data[index - 1].param) {
            // 查找到符合条件的数据时每次要把之前存储的数据+1
            this.spanArr[this.pos] += 1;
            this.spanArr.push(0);
          } else {
            // 没有符合的数据时，要记住当前的index
            this.spanArr.push(1);
            this.pos = index;
          }
        }
      });
    },
    objectSpanMethod({ row, column, rowIndex, columnIndex }) {
      // 页面列表上 表格合并行 -> 第几列(从0开始)
      // 需要合并多个单元格时 依次增加判断条件即可
      if (columnIndex === 0) {
        // 二维数组存储的数据 取出
        const _row = this.spanArr[rowIndex];
        const _col = _row > 0 ? 1 : 0;
        return {
          rowspan: _row,
          colspan: _col,
        };
        //不可以return {rowspan：0， colspan: 0} 会造成数据不渲染， 也可以不写else，eslint过不了的话就返回false
      } else {
        return false;
      }
    },
  },
};



