class Storage {
    constructor(props) {
        this.props = props || {}
        this.source = this.props.source || window.localStorage
        this.initRun()
    }
    initRun() {
        const reg = new RegExp('expires')
        let data = this.source
        let list = Object.keys(data)
        if (list.length > 0) {
            list.map((key, v) => {
